			{{peopleLikesByAmount = peopleLikesByAmount.sort(function(a, b) { if (b.likes > a.likes) return -1; return a.likes == b.likes ? 0 : 1});}}
			{{var f = 1 / peopleLikesByAmount.length;}}
			{{~peopleLikesByAmount :like:i}}
				{{var c = parseInt(i * f * 240) + 10;}}
